import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import Sidebar from 'parts/Sidebar'
import courses from 'constants/api/courses'
import {statusCourses, fetchCourses, messageCourse} from 'store/actions/courses'

function EmptyState() {
  return <section className="flex h-screen items-center">
    <div className="text-center py-12 mx-auto w-5/12">
      <img src={`${process.env.PUBLIC_URL}/assets/images/illustration-myclass-empty.jpg`} alt="success join class"/>
        <h1 className="text-3xl text-gray-900 mt-12">Time to Invest</h1>
        <p className="text-lg text-gray-900 mt-4 mb-8 mx-auto text-center">
          It seems you don't have any class yet so let's get them and grow your skill 
        </p>
      <a
        target="_black"
        rel="noopener noreferrer "
        className="bg-orange-500 hover:bg-orange-400 transition-all duration-200 focus:outline-none shadow-inner 
      text-white px-6 py-3 mt-5" 
        href={`${process.env.REACT_APP_FRONTPAGE_URL}/library`} >
        Cari Kelas
      </a>
    </div>
  </section>
}

export default function MyClass() {

  const dispatch = useDispatch()

  useEffect(() => {
    window.scroll(0,0);

    dispatch(statusCourses("loading"))
    courses.mine().then( res => {
      console.log(res)
      dispatch(fetchCourses(res))
    }).catch(err => {
      dispatch(messageCourse(err?.response))
    })

    return () => {
      cleanup
    }
  }, [])

  return (
    <div className="flex">
      <Sidebar></Sidebar>
      <main className="flex-1">
        <div className="px-16">
          <EmptyState></EmptyState>
        </div>
      </main>
    </div>
  )
}
